 if (plr~=player) then table.insert(playerNames,plr.Name);end end local selectedPlayerName=playerNames[1] or nil ;infoTab:AddDropdown({Name="Players List",Description="Select a player",Options=playerNames,Default=playerNames[1] or "" ,Callback=function(Value) selectedPlayerName=Value;print("Selected Player: "   .. tostring(selectedPlayerName) );end});infoTab:AddButton({Name="Copy Skin",Callback=function() if  not selectedPlayerName then print("Select a player first");return;end local targetPlayer=Players:FindFirstChild(selectedPlayerName);if ( not targetPlayer or  not targetPlayer.Character) then print("Player not found or character not loaded");return;end for _,item in pairs(targetPlayer.Character:GetChildren()) do if (item:IsA("Accessory") or item:IsA("Hat") or item:IsA("Tool")) then item:Clone().Parent=player.Backpack;end end print("Copied skin/tools from "   .. selectedPlayerName );end});infoTab:AddSection({"Script System â†“"});infoTab:AddSlider({Name="UI Scale",Min=0.5,Max=2,Increase=0.05,Default=1,Callback=function(Value) if Window.MainFrame then Window.MainFrame.Size=UDim2.fromScale(Value,Value);end end});infoTab:AddDropdown({Name="Select Theme",Description="Change script theme colors",Options={"Dark","Darker","Purple"},Default="Dark",Callback=function(Value) redzlib:SetTheme(Value);print("Theme changed to "   .. Value );end});local brainrotTab=Window:MakeTab({"StaelBrainrot","sword"});local checkpoint=nil;local walkingToCheckpoint=false;brainrotTab:AddButton({Name="Save Checkpoint",Callback=function() checkpoint=humanoidRootPart.Position;print("Checkpoint Saved!");end});brainrotTab:AddButton({Name="Walk to Checkpoint",Callback=function() if checkpoint then walkingToCheckpoint=true;print("Walking to checkpoint...");else print("No checkpoint saved yet!");end end});brainrotTab:AddButton({Name="Stop Walking",Callback=function() walkingToCheckpoint=false;humanoid.WalkSpeed=60;print("Stopped walking.");end});brainrotTab:AddButton({Name="Set Random Player Speed",Callback=function() local allPlayers=Players:GetPlayers();local targetPlayers={};for _,p in ipairs(allPlayers) do if (p~=player) then table.insert(targetPlayers,p);end end if ( #targetPlayers>0) then local targetPlayer=targetPlayers[math.random(1, #targetPlayers)];if (targetPlayer and targetPlayer.Character and targetPlayer.Character:FindFirstChild("Humanoid")) then targetPlayer.Character.Humanoid.WalkSpeed=50;print("Speed set to 50 for "   .. targetPlayer.Name );else print("Could not modify target player: "   .. targetPlayer.Name );end else print("No other players found in the server!");end end});brainrotTab:AddParagraph({"More","Coming soon "});local testTab=Window:MakeTab({"BombTest","user"});testTab:AddButton({Name="Go Up",Callback=function() if humanoidRootPart then humanoidRootPart.CFrame=humanoidRootPart.CFrame + Vector3.new(0,50,0) ;print("Teleported Up");end end});testTab:AddButton({Name="Go Down",Callback=function() if humanoidRootPart then humanoidRootPart.CFrame=humanoidRootPart.CFrame-Vector3.new(0,50,0) ;print("Teleported Down");end end});local ScreenGui=Instance.new("ScreenGui",playerGui);ScreenGui.Name="MiniTeleportUI";ScreenGui.Enabled=false;local Frame=Instance.new("Frame",ScreenGui);Frame.Size=UDim2.new(0,150,0,80);Frame.Position=UDim2.new(0.5, -75,0.5, -40);Frame.BackgroundColor3=Color3.fromRGB(30,30,30);Frame.Active=true;Frame.Draggable=true;local TeleportButton=Instance.new("TextButton",Frame);TeleportButton.Size=UDim2.new(1, -20,0,40);TeleportButton.Position=UDim2.new(0,10,0.5, -20);TeleportButton.Text="Teleport";TeleportButton.TextColor3=Color3.fromRGB(255,255,255);TeleportButton.BackgroundColor3=Color3.fromRGB(60,60,60);TeleportButton.MouseButton1Click:Connect(function() if  not humanoidRootPart then return;end local closestPlayer,closestDist=nil,math.huge;for _,other in pairs(Players:GetPlayers()) do if ((other~=player) and other.Character and other.Character:FindFirstChild("HumanoidRootPart")) then local dist=(humanoidRootPart.Position-other.Character.HumanoidRootPart.Position).Magnitude;if (dist<closestDist) then closestDist=dist;closestPlayer=other;end end end if closestPlayer then humanoidRootPart.CFrame=closestPlayer.Character.HumanoidRootPart.CFrame * CFrame.new(0,0, -3) ;print("Teleported to: "   .. closestPlayer.Name );else print("No players found");end end);testTab:AddToggle({Name="Mini Teleport UI",Default=false,Callback=function(v) ScreenGui.Enabled=v;end});testTab:AddParagraph({"More","Coming soon"});RunService.Stepped:Connect(function() if (walkingToCheckpoint and checkpoint) then humanoid:MoveTo(checkpoint);for _,part in pairs(character:GetDescendants()) do if part:IsA("BasePart") then part.CanCollide=false;end end if ((humanoidRootPart.Position-checkpoint).Magnitude<4) then walkingToCheckpoint=false;print("Arrived at checkpoint.");end end if noclipEnabled then for _,part in pairs(character:GetDescendants()) do if part:IsA("BasePart") then part.CanCollide=false;end end end end); end
